package rahulprasad.operators;

public class prepost_increment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 3;
		i++;              //post_increment
		//prints 4
		System.out.println(i);
		++i;               //pre_increment
		//prints 5
		System.out.println(i);
		//prints 6
		System.out.println(++i);
		//prints 6
		System.out.println(i++);
		//prints 7
		System.out.println(i);
	}

}
